import React from 'react';

function Header({ setPage, activePage }) {
    return (
      <div className="bg-red text-white text-xl flex items-center justify-between px-4 py-2">
        <h1 className="text-gold font-bold text-2xl">CHANSE SHIRLEY</h1>
        <p
          className={`cursor-pointer hover:text-gold transition ${
            activePage === 'about' ? 'text-gold' : ''
          }`}
          onClick={() => setPage('about')}
        >
          About Me
        </p>
        <p
          className={`cursor-pointer hover:text-gold transition ${
            activePage === 'portfolio' ? 'text-gold' : ''
          }`}
          onClick={() => setPage('portfolio')}
        >
          Portfolio
        </p>
        <p
          className={`cursor-pointer hover:text-gold transition ${
            activePage === 'resume' ? 'text-gold' : ''
          }`}
          onClick={() => setPage('resume')}
        >
          Resume
        </p>
        <p
          className={`cursor-pointer hover:text-gold transition ${
            activePage === 'contact' ? 'text-gold' : ''
          }`}
          onClick={() => setPage('contact')}
        >
          Contact
        </p>
      </div>
    );
  }
  
  export default Header;
