import React from 'react';

function Header({ handlePage, activePage }) {

    // Utility function to check if a page is active
    const isActivePage = (pageName) => {
        return activePage === pageName ? 'text-gold' : 'text-white';
    };

    return (
        <div className='bg-red text-white text-xl flex items-center justify-between px-4 py-2'>
            <h1 className='text-gold font-bold text-3xl'>CHANSE SHIRLEY</h1>
            <p className={`cursor-pointer hover:text-gold transition ${isActivePage('about')}`} onClick={() => handlePage('about')}>About Me</p>
            <p className={`cursor-pointer hover:text-gold transition ${isActivePage('portfolio')}`} onClick={() => handlePage('portfolio')}>Portfolio</p>
            <p className={`cursor-pointer hover:text-gold transition ${isActivePage('resume')}`} onClick={() => handlePage('resume')}>Resume</p>
            <p className={`cursor-pointer hover:text-gold transition ${isActivePage('contact')}`} onClick={() => handlePage('contact')}>Contact</p>
        </div>
    )

}

export default Header;
