import React, { useState } from 'react';

function Header({ handlePage }) {
  const [activePage, setActivePage] = useState('about');

  return (
    <div className='bg-red text-white text-xl flex items-center justify-between px-4 py-2'>
      <h1 className='text-gold font-bold text-3xl'>CHANSE SHIRLEY</h1>
      <p
        className={`cursor-pointer hover:text-gold transition ${
          activePage === 'about' ? 'text-gold' : ''
        }`}
        onClick={() => {
          handlePage('about');
          setActivePage('about');
        }}
      >
        About Me
      </p>
      <p
        className={`cursor-pointer hover:text-gold transition ${
          activePage === 'portfolio' ? 'text-gold' : ''
        }`}
        onClick={() => {
          handlePage('portfolio');
          setActivePage('portfolio');
        }}
      >
        Portfolio
      </p>
      <p
        className={`cursor-pointer hover:text-gold transition ${
          activePage === 'resume' ? 'text-gold' : ''
        }`}
        onClick={() => {
          handlePage('resume');
          setActivePage('resume');
        }}
      >
        Resume
      </p>
      <p
        className={`cursor-pointer hover:text-gold transition ${
          activePage === 'contact' ? 'text-gold' : ''
        }`}
        onClick={() => {
          handlePage('contact');
          setActivePage('contact');
        }}
      >
        Contact
      </p>
    </div>
  );
}

export default Header;
